import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const quizData = {
  Python: {
    1: [
      { question: "Who created Python?", options: ["Dennis Ritchie", "Guido van Rossum", "James Gosling", "Bjarne Stroustrup"], answer: "Guido van Rossum" },
      { question: "Which Python version introduced pattern matching?", options: ["Python 2.7", "Python 3.6", "Python 3.9", "Python 3.10"], answer: "Python 3.10" },
    ],
  },
  Java: {
    1: [
      { question: "Who developed Java?", options: ["Dennis Ritchie", "Bjarne Stroustrup", "James Gosling", "Guido van Rossum"], answer: "James Gosling" },
      { question: "What is the main purpose of the Java Virtual Machine (JVM)?", options: ["To compile Java code into machine code", "To execute Java bytecode on any platform", "To debug Java programs", "To create user interfaces in Java"], answer: "To execute Java bytecode on any platform" },
    ],
  },
};

const shuffleArray = (array) => [...array].sort(() => Math.random() - 0.5);

const getRandomQuestions = (questions, num) => {
  if (!questions) return [];
  let shuffledQuestions = shuffleArray(questions);
  return shuffledQuestions.slice(0, num).map(q => ({
    ...q,
    options: shuffleArray(q.options),
  }));
};

const Quiz = () => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);

  useEffect(() => {
    const moduleNum = parseInt(moduleId, 10); // Convert moduleId to integer

    if (!quizData[courseName] || !quizData[courseName][moduleNum]) {
      console.error("Quiz data not found!");
      setQuestions([]);
    } else {
      setQuestions(getRandomQuestions(quizData[courseName][moduleNum], 5));
    }
  }, [courseName, moduleId]);

  const handleAnswer = (option) => {
    setSelectedAnswer(option);

    if (option === questions[currentIndex].answer) {
      setScore(prev => prev + 1);
    }

    setTimeout(() => {
      if (currentIndex + 1 < questions.length) {
        setCurrentIndex(prev => prev + 1);
        setSelectedAnswer(null);
      } else {
        handleQuizCompletion();
      }
    }, 1000);
  };

  const handleQuizCompletion = () => {
    if (score >= 3) {
      alert(`ðŸŽ‰ Congratulations! You scored ${score}/5. Moving to the next module.`);
      navigate(`/course/${courseName}/${parseInt(moduleId) + 1}`);
    } else {
      alert(`ðŸ˜¢ You scored ${score}/5. You must retake Module ${moduleId}.`);
      navigate(`/course/${courseName}/${moduleId}`);
    }
  };

  if (!questions.length) {
    return <p>No quiz available for this module.</p>;
  }

  return (
    <div>
      <h2>Quiz for Module {moduleId}</h2>
      <p>Question {currentIndex + 1} of {questions.length}</p>
      <p><strong>{questions[currentIndex].question}</strong></p>
      <ul>
        {questions[currentIndex].options.map((option, idx) => (
          <li
            key={idx}
            onClick={() => handleAnswer(option)}
            style={{
              cursor: "pointer",
              background: selectedAnswer
                ? option === questions[currentIndex].answer
                  ? "lightgreen"
                  : option === selectedAnswer
                    ? "red"
                    : "white"
                : "white",
              padding: "10px",
              border: "1px solid black",
              margin: "5px",
              borderRadius: "5px",
            }}
          >
            {option}
          </li>
        ))}
      </ul>
      <p>Score: {score}</p>
    </div>
  );
};

export default Quiz;
