import { useLocation, useNavigate } from "react-router-dom";
import "./conclusion.css";

const Conclusion = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { wrongAnswers = [], score, courseName, moduleId } = state || {};

  // Function to get explanations for questions
  const getExplanation = (question) => {
    const explanations = {
      "What is a variable in Python?": "A variable is a container for storing data values.",
      "What is the use of the 'if' statement?": "It is used to execute a block of code only if a specified condition is true.",
      // Add more explanations here
    };
    return explanations[question] || "No explanation available.";
  };

  return (
    <div className="conclusion-container">
      <h2>Quiz Summary</h2>
      <p className="final-score">Your Final Score: {score}/10</p>

      {wrongAnswers.length > 0 ? (
        <>
          <h3>Review Your Mistakes:</h3>
          <ul className="mistakes-list">
            {wrongAnswers.map((item, index) => (
              <li key={index} className="mistake-item">
                <p><strong>Question:</strong> {item.question}</p>
                <p><strong>Your Answer:</strong> <span className="wrong-answer">{item.selected}</span></p>
                <p><strong>Correct Answer:</strong> <span className="correct-answer">{item.correct}</span></p>
                <p><strong>Explanation:</strong> {getExplanation(item.question)}</p>
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p className="perfect-score">Great job! You got all the answers correct.</p>
      )}

      <button
        className="next-module-button"
        onClick={() => navigate(`/course/${courseName}/${parseInt(moduleId) + 1}`)}
      >
        Go to Next Module
      </button>
    </div>
  );
};

export default Conclusion;
