// Function to shuffle an array
const shuffleArray = (array) => {
  return [...array].sort(() => Math.random() - 0.5);
};

// Function to get 10 random questions from a module's 15 questions
const getRandomQuestions = (questions) => {
  return shuffleArray(questions).slice(0, 10).map((q) => ({
    ...q,
    options: shuffleArray(q.options),
  }));
};

const Quiz = () => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);

  useEffect(() => {
    if (!quizData[courseName] || !quizData[courseName][moduleId]) {
      console.error("Quiz data not found!");
      setQuestions([]);
    } else {
      setQuestions(getRandomQuestions(quizData[courseName][moduleId], 10));
    }
  }, [courseName, moduleId]);

  const handleAnswer = (option) => {
    setSelectedAnswer(option);
    if (option === questions[currentIndex].answer) {
      setScore((prev) => prev + 1);
    }
    setTimeout(() => {
      if (currentIndex + 1 < questions.length) {
        setCurrentIndex((prev) => prev + 1);
        setSelectedAnswer(null);
      } else {
        handleQuizCompletion();
      }
    }, 1000);
  };

  const handleQuizCompletion = () => {
    const nextModule = parseInt(moduleId) + (score >= 7 ? 1 : -1);
    alert(score >= 7 ? `üéâ Passed! Score: ${score}/10` : `‚ùå Failed! Score: ${score}/10`);
    navigate(`/course/${courseName}/${Math.max(1, Math.min(6, nextModule))}`);
  };

  return questions.length === 0 ? <p>No quiz available.</p> : (
    <div>
      <h2>Quiz for {courseName} - Module {moduleId}</h2>
      <p>Question {currentIndex + 1} of {questions.length}</p>
      <p><strong>{questions[currentIndex].question}</strong></p>
      <ul>
        {questions[currentIndex].options.map((option, idx) => (
          <li
            key={idx}
            onClick={() => handleAnswer(option)}
            style={{
              backgroundColor: selectedAnswer
                ? option === questions[currentIndex].answer
                  ? "lightgreen"
                  : option === selectedAnswer
                  ? "red"
                  : "white"
                : "white",
              cursor: "pointer",
              padding: "10px",
              border: "1px solid black",
              margin: "5px",
              borderRadius: "5px",
            }}
          >
            {option}
          </li>
        ))}
      </ul>
      <p>Score: {score}</p>
    </div>
  );
};

export default Quiz;
