import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Quiz = () => {
  const { courseName, moduleId } = useParams();
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!courseName || !moduleId) return;

    const formattedModule = `${courseName.toLowerCase()}_${moduleId}`;

    fetch(`http://127.0.0.1:8000/generate_mcq/${formattedModule}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.mcqs && Array.isArray(data.mcqs)) {
          setQuestions(data.mcqs);
        } else {
          throw new Error("Invalid question format received.");
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching MCQs:", error);
        setError("Failed to load questions. Please try again.");
        setLoading(false);
      });
  }, [courseName, moduleId]);

  if (loading) return <p>Loading questions...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (questions.length === 0) return <p>No questions available.</p>;

  return (
    <div>
      <h2>Quiz for {courseName} - Module {moduleId}</h2>
      <ol>
        {questions.map((q, index) => (
          <li key={index}>
            <p>{q.text}</p>
            <ul>
              {q.options.map((option, i) => (
                <li key={i}>{option}</li>
              ))}
            </ul>
          </li>
        ))}
      </ol>
    </div>
  );
};

export default Quiz;
