import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./Course.css";

// Define courses and their respective modules
const courses = {
  Python: [
    { id: 1, title: "Introduction to Python Programming", file: "/courses/module_1.md" },
    { id: 2, title: "Strings, Lists, and Dictionaries", file: "/courses/module_2.md" },
    { id: 3, title: "Functions and Classes", file: "/courses/module_3.md" },
    { id: 4, title: "Simple Graphics and Image Processing", file: "/courses/module_4.md" },
    { id: 5, title: "Graphical User Interfaces", file: "/courses/module_5.md" },
    { id: 6, title: "Multithreading, Networks, and Client/Server Programming", file: "/courses/module_6.md" },
  ],
  Java: [
    { id: 1, title: "Java Basics", file: "/courses/jmodule_1.md" },
    { id: 2, title: "Object-Oriented Programming (OOP)", file: "/courses/java_module_2.md" },
    { id: 3, title: "Java Collections Framework", file: "/courses/java_module_3.md" },
    { id: 4, title: "Exception Handling", file: "/courses/java_module_4.md" },
    { id: 5, title: "Multithreading and Concurrency", file: "/courses/java_module_5.md" },
    { id: 6, title: "Java I/O and File Handling", file: "/courses/java_module_6.md" }
  ],
};

const Course = () => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const moduleNumber = parseInt(moduleId, 10);
  const modules = courses[courseName];

  useEffect(() => {
    if (!modules) {
      console.error("Invalid course:", courseName);
      navigate("/survey");
      return;
    }

    const currentModule = modules.find((m) => m.id === moduleNumber);
    if (!currentModule) {
      console.error("Invalid module:", moduleId);
      navigate(`/course/${courseName}/1`);
      return;
    }

    // Fetch module content
    fetch(currentModule.file)
      .then((response) => response.text())
      .then((data) => setContent(data))
      .catch((error) => console.error("Error loading module:", error));
  }, [courseName, moduleId, navigate]);

  return (
    <div className="course-container">
      <h1 className="course-title">{modules?.find((m) => m.id === moduleNumber)?.title || "Module Not Found"}</h1>
      
      <div className="course-content">
        <ReactMarkdown>{content || "Loading..."}</ReactMarkdown>
      </div>

      <div className="navigation-buttons">
        {moduleNumber > 1 && (
          <button onClick={() => navigate(`/course/${courseName}/${moduleNumber - 1}`)}>⬅ Prev</button>
        )}
        {moduleNumber < modules.length && (
          <button onClick={() => navigate(`/course/${courseName}/${moduleNumber + 1}`)}>Next ➡</button>
        )}
      </div>
    </div>
  );
};

export default Course;
