import React, { useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const FileUpload = ({ onQuizGenerated }) => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) {
      alert("Please upload a file.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    try {
      // Send the file to the backend for quiz generation
      const response = await axios.post("http://localhost:5000/generate-quiz", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      // Save the generated quiz in localStorage
      localStorage.setItem(`${courseName}_module_${moduleId}_quiz`, JSON.stringify(response.data.quiz));

      // Notify the parent component (Course) that the quiz has been generated
      onQuizGenerated(response.data.quiz);

      // Navigate to the quiz page
      navigate(`/quiz/${courseName}/${moduleId}`);
    } catch (error) {
      console.error("Error generating quiz:", error);
      alert("Failed to generate quiz.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="file-upload-container">
      <h2>Upload Module Content</h2>
      <input type="file" onChange={handleFileChange} accept=".pdf,.md" />
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? "Generating Quiz..." : "Generate Quiz"}
      </button>
    </div>
  );
};

export default FileUpload;
