import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./Quiz.css";

const Quiz = () => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState("");
  const [score, setScore] = useState(0);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/get-quiz/${courseName}/${moduleId}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.quiz) {
          setQuiz(data.quiz);
        } else {
          console.error("Error:", data.error);
        }
      });
  }, [courseName, moduleId]);

  return (
    <div className="quiz-container">
      <h1>Quiz for {courseName} - Module {moduleId}</h1>
      <ReactMarkdown>{quiz || "Loading..."}</ReactMarkdown>
      <button onClick={() => navigate(`/course/${courseName}/${moduleId}`)}>ðŸ“š Back to Course</button>
    </div>
  );
};

export default Quiz;
