import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";

const Assessment = () => {
    // Get query parameters from the URL
    const [searchParams, setSearchParams] = useSearchParams();
    const initialCourse = searchParams.get("course") || "Java";
    const initialLevel = searchParams.get("level") || "Beginner";

    // State for course, level, and questions
    const [selectedCourse, setSelectedCourse] = useState(initialCourse);
    const [selectedLevel, setSelectedLevel] = useState(initialLevel);
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Fetch questions when course/level changes
    useEffect(() => {
        const fetchQuestions = async () => {
            setLoading(true);
            try {
                const response = await axios.post("http://localhost:5001/generate-mcq", {
                    course: selectedCourse,
                    level: selectedLevel
                });
                setQuestions(response.data.questions);
                setError(null);
            } catch (err) {
                setError("Failed to load questions.");
            } finally {
                setLoading(false);
            }
        };

        fetchQuestions();
    }, [selectedCourse, selectedLevel]);

    // Function to update query parameters and reload questions
    const updateSelection = () => {
        setSearchParams({ course: selectedCourse, level: selectedLevel });
    };

    return (
        <div className="quiz-container">
            <h2>ðŸ“š {selectedCourse} - {selectedLevel} Level Assessment</h2>

            <div className="selection-container">
                <label>Course: </label>
                <select value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)}>
                    <option value="Java">Java</option>
                    <option value="Python">Python</option>
                </select>

                <label>Level: </label>
                <select value={selectedLevel} onChange={(e) => setSelectedLevel(e.target.value)}>
                    <option value="Beginner">Beginner</option>
                    <option value="Intermediate">Intermediate</option>
                    <option value="Advanced">Advanced</option>
                </select>

                <button onClick={updateSelection}>Start</button>
            </div>

            {loading && <p>Loading questions...</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}

            {!loading && !error && questions.length > 0 && (
                <div className="questions-container">
                    {questions.map((q, index) => (
                        <div key={index} className="question">
                            <p><strong>Q{index + 1}:</strong> {q.text}</p>
                            <ul>
                                {q.options.map((option, idx) => (
                                    <li key={idx}><input type="radio" name={`q${index}`} /> {option}</li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            )}

            {!loading && questions.length === 0 && <p>No questions available for this selection.</p>}
        </div>
    );
};

export default Assessment;
