import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./Assessment.css"; 

const Assessment = () => {
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const course = queryParams.get("course") || "Machine Learning";
    const level = queryParams.get("level") || "Beginner";

    const [questions, setQuestions] = useState([]);
    const [userAnswers, setUserAnswers] = useState({});
    const [loading, setLoading] = useState(true);
    const [submitted, setSubmitted] = useState(false);
    const [score, setScore] = useState(0);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchQuestions = async () => {
            setLoading(true);
            setError(null);

            try {
                const response = await axios.post("http://localhost:5001/generate-mcq", { course, level });

                if (response.data && Array.isArray(response.data.questions)) {
                    setQuestions(response.data.questions);
                } else {
                    throw new Error("Invalid data format received from server");
                }
            } catch (err) {
                console.error("Error fetching questions:", err);
                setError("Failed to load questions. Please try again.");
            }
            setLoading(false);
        };

        fetchQuestions();
    }, []);  // Dependency removed to prevent unnecessary API calls

    const handleAnswerSelection = (questionIndex, selectedOption) => {
        setUserAnswers((prev) => ({
            ...prev,
            [questionIndex]: selectedOption,
        }));
    };

    const handleSubmit = () => {
        let correctCount = 0;
        questions.forEach((q, index) => {
            if (userAnswers[index] === q.correct) {
                correctCount++;
            }
        });

        setScore(correctCount);
        setSubmitted(true);
    };

    return (
        <div className="quiz-container">
            <h2>ðŸ“š {course} - {level} Level Assessment</h2>
            
            {loading ? (
                <p>Loading questions...</p>
            ) : error ? (
                <p className="error-message">{error}</p>
            ) : (
                <div>
                    {questions.map((q, index) => (
                        <div key={index} className="question-card">
                            <h3>{index + 1}. {q.text}</h3>
                            <div className="options-container">
                                {q.options.map((option, optIndex) => (
                                    <label key={optIndex} className="option-label">
                                        <input
                                            type="radio"
                                            name={`question-${index}`}
                                            value={option}
                                            checked={userAnswers[index] === option}
                                            onChange={() => handleAnswerSelection(index, option)}
                                            disabled={submitted} 
                                        />
                                        {option}
                                    </label>
                                ))}
                            </div>
                        </div>
                    ))}
                    
                    {!submitted ? (
                        <button className="submit-btn" onClick={handleSubmit}>Submit</button>
                    ) : (
                        <div className="result-container">
                            <h3>âœ… You scored {score} / {questions.length}</h3>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Assessment;
