import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./Course.css";

const courses = {
  Python: [
    { id: 1, title: "Introduction to Python Programming", file: "/courses/module_1.md" },
    { id: 2, title: "Strings, Lists, and Dictionaries", file: "/courses/module_2.md" },
    { id: 3, title: "Functions and Classes", file: "/courses/module_3.md" },
    { id: 4, title: "Simple Graphics and Image Processing", file: "/courses/module_4.md" },
    { id: 5, title: "Graphical User Interfaces", file: "/courses/module_5.md" },
    { id: 6, title: "Multithreading, Networks, and Client/Server Programming", file: "/courses/module_6.md" },
  ],
  Java: [
    { id: 1, title: "Introduction to Java Programming", file: "/courses/jmodule_1.md" },
    { id: 2, title: "Object-Oriented Programming in Java", file: "/courses/jmodule_2.md" },
    { id: 3, title: "Exception Handling and File I/O", file: "/courses/jmodule_3.md" },
    { id: 4, title: "Multithreading and Concurrency", file: "/courses/jmodule_4.md" },
    { id: 5, title: "Java GUI Development (Swing & JavaFX)", file: "/courses/jmodule_5.md" },
    { id: 6, title: "Spring Boot and Web Development", file: "/courses/jmodule_6.md" },
  ],
};

const Course = () => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState("");
  const [moduleCompleted, setModuleCompleted] = useState(false);

  const moduleNumber = parseInt(moduleId, 10);
  const modules = courses[courseName] || [];

  useEffect(() => {
    if (!modules.length) {
      console.error("Invalid course:", courseName);
      navigate("/survey");
      return;
    }

    const currentModule = modules.find((m) => m.id === moduleNumber);
    if (!currentModule) {
      console.error("Invalid module:", moduleId);
      navigate(`/course/${courseName}/1`);
      return;
    }

    fetch(currentModule.file)
      .then((response) => {
        if (!response.ok) throw new Error(`Failed to load ${currentModule.file}`);
        return response.text();
      })
      .then((data) => setContent(data))
      .catch((error) => console.error("Error loading module:", error));
  }, [courseName, moduleNumber, navigate]);

  // Toggle completion status
  const handleCompleteModule = () => {
    setModuleCompleted((prev) => !prev);
    alert(moduleCompleted ? "Module marked as incomplete." : "Module marked as complete!");
  };

  const handleTakeQuiz = () => {
    if (!moduleCompleted) {
      alert("Please complete the module before starting the quiz.");
      return;
    }
    navigate(`/course/${courseName}/${moduleId}/quiz`);
  };

  return (
    <div className="course-container">
      <h1 className="course-title">{modules[moduleNumber - 1]?.title || "Module Not Found"}</h1>

      <div className="course-content">
        <ReactMarkdown>{content || "Loading..."}</ReactMarkdown>
      </div>

      <div className="action-buttons">
        <button onClick={handleCompleteModule} className={`complete-button ${moduleCompleted ? "completed" : ""}`}>
          {moduleCompleted ? "âœ” Marked as Complete" : "Mark as Completed"}
        </button>

        <button onClick={handleTakeQuiz} className="quiz-button" disabled={!moduleCompleted}>
          ðŸŽ¯ Take Quiz for This Module
        </button>
      </div>
    </div>
  );
};

export default Course;
