import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const quizData = {
  Python: {
    1: [
      { question: "Who created Python?", options: ["Guido van Rossum", "James Gosling", "Dennis Ritchie", "Bjarne Stroustrup"], answer: "Guido van Rossum" },
      { question: "Which Python version introduced pattern matching?", options: ["Python 2.7", "Python 3.6", "Python 3.9", "Python 3.10"], answer: "Python 3.10" },
      { question: "What is NOT a key feature of Python?", options: ["Readability", "Dynamically typed", "Strong typing", "Complex syntax"], answer: "Complex syntax" },
    ],
    2: [
      { question: "What does the `len()` function do in Python?", options: ["Counts items in a list", "Finds maximum value", "Checks if a variable is None", "Reverses a string"], answer: "Counts items in a list" },
      { question: "What will `5 ** 2` return in Python?", options: ["10", "25", "5", "2.5"], answer: "25" },
      { question: "Which statement is used for loops in Python?", options: ["for", "loop", "repeat", "iterate"], answer: "for" },
    ],
    3: [
      { question: "Which of these is not a Python data type?", options: ["List", "Tuple", "Class", "Enum"], answer: "Enum" },
      { question: "What does the `continue` statement do?", options: ["Exits the loop", "Skips the current iteration", "Repeats the loop", "Ends the function"], answer: "Skips the current iteration" },
      { question: "What does `self` refer to in Python classes?", options: ["The current class", "The previous object", "The current instance", "The superclass"], answer: "The current instance" },
    ],
  },

  Java: {
    1: [
      { question: "Who created Java?", options: ["Brendan Eich", "James Gosling", "Dennis Ritchie", "Bjarne Stroustrup"], answer: "James Gosling" },
      { question: "Which keyword is used to define a class in Java?", options: ["class", "Class", "define", "struct"], answer: "class" },
      { question: "What is the default value of an int variable in Java?", options: ["0", "null", "undefined", "1"], answer: "0" },
    ],
    2: [
      { question: "Which collection class allows elements to be accessed using a key?", options: ["List", "Queue", "Map", "Set"], answer: "Map" },
      { question: "What is the correct syntax for a main method in Java?", options: ["public static void main(String args[])", "void main(String args[])", "int main()", "main()"], answer: "public static void main(String args[])" },
      { question: "Which package is imported by default in every Java program?", options: ["java.util", "java.io", "java.lang", "java.net"], answer: "java.lang" },
    ],
    3: [
      { question: "What is the superclass of all Java classes?", options: ["Object", "Main", "Super", "Base"], answer: "Object" },
      { question: "Which operator is used for bitwise AND in Java?", options: ["&&", "&", "||", "|"], answer: "&" },
      { question: "Which keyword is used to inherit a class in Java?", options: ["this", "extends", "implements", "super"], answer: "extends" },
    ],
  },
};

// Function to shuffle an array
const shuffleArray = (array) => {
  return [...array].sort(() => Math.random() - 0.5);
};

// Function to get a limited number of random questions
const getRandomQuestions = (questions, num) => {
  return [...questions]
    .sort(() => Math.random() - 0.5)
    .slice(0, num)
    .map((q) => ({
      ...q,
      options: shuffleArray(q.options),
    }));
};

const Quiz = () => {
  const { courseName, moduleId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);

  useEffect(() => {
    if (!quizData[courseName] || !quizData[courseName][moduleId]) {
      console.error("Quiz data not found!");
      setQuestions([]);
    } else {
      setQuestions(getRandomQuestions(quizData[courseName][moduleId], 3)); // Getting 3 random questions per module
    }
  }, [courseName, moduleId]);

  const handleAnswer = (option) => {
    setSelectedAnswer(option);

    if (option === questions[currentIndex].answer) {
      setScore((prev) => prev + 1);
    }

    setTimeout(() => {
      if (currentIndex + 1 < questions.length) {
        setCurrentIndex((prev) => prev + 1);
        setSelectedAnswer(null);
      } else {
        handleQuizCompletion();
      }
    }, 1000);
  };

  const handleQuizCompletion = () => {
    if (score >= 2) {
      alert(`ðŸŽ‰ Congratulations! You scored ${score}/3. Moving to the next module.`);
      navigate(`/course/${courseName}/${parseInt(moduleId) + 1}`);
    } else {
      alert(`ðŸ˜¢ You scored ${score}/3. You must retake Module ${moduleId}.`);
      navigate(`/course/${courseName}/${moduleId}`);
    }
  };

  if (questions.length === 0) {
    return <p>No quiz available for this module.</p>;
  }

  return (
    <div>
      <h2>Quiz for {courseName} - Module {moduleId}</h2>
      <p>Question {currentIndex + 1} of {questions.length}</p>
      <p><strong>{questions[currentIndex].question}</strong></p>
      <ul>
        {questions[currentIndex].options.map((option, idx) => (
          <li
            key={idx}
            onClick={() => handleAnswer(option)}
            style={{
              backgroundColor: selectedAnswer
                ? option === questions[currentIndex].answer
                  ? "lightgreen"
                  : option === selectedAnswer
                  ? "red"
                  : "white"
                : "white",
              cursor: "pointer",
              padding: "10px",
              border: "1px solid black",
              margin: "5px",
              borderRadius: "5px",
            }}
          >
            {option}
          </li>
        ))}
      </ul>
      <p>Score: {score}</p>
    </div>
  );
};

export default Quiz;
