import React, { useState, useEffect } from "react";

const Quiz = ({ moduleName }) => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!moduleName) return; // Ensure moduleName is valid before fetching

    fetch(`http://127.0.0.1:8000/generate_mcq/${moduleName}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        try {
          const mcqs = typeof data.mcqs === "string" ? JSON.parse(data.mcqs) : data.mcqs;
          setQuestions(mcqs);
        } catch (error) {
          setError("Failed to parse questions.");
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching MCQs:", error);
        setError("Failed to load questions.");
        setLoading(false);
      });
  }, [moduleName]);

  if (loading) return <p>Loading questions...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (questions.length === 0) return <p>No questions available.</p>;

  return (
    <div>
      <h2>{moduleName} Quiz</h2>
      <ol>
        {questions.map((q, index) => (
          <li key={index}>
            <p>{q.text}</p>
            <ul>
              {q.options.map((option, i) => (
                <li key={i}>{option}</li>
              ))}
            </ul>
          </li>
        ))}
      </ol>
    </div>
  );
};

export default Quiz;
