import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./Assessment.css"; 

const Assessment = () => {
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const course = queryParams.get("course");
    const level = queryParams.get("level");

    const [questions, setQuestions] = useState([]);
    const [answers, setAnswers] = useState({});
    const [loading, setLoading] = useState(true);
    const [score, setScore] = useState(null);
    const [summary, setSummary] = useState("");

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                console.log("Fetching questions for:", course, level);
                
                const response = await axios.post("http://localhost:5001/generate-mcq", { 
                    course, 
                    level 
                });

                console.log("MCQs received:", response.data);
                
                setQuestions(response.data.questions);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching questions:", error);
                setLoading(false);
            }
        };

        fetchQuestions();
    }, [course, level]);

    const handleAnswerChange = (question, selectedOption) => {
        setAnswers((prevAnswers) => ({
            ...prevAnswers,
            [question]: selectedOption
        }));
    };

    const handleSubmit = async () => {
        console.log("User Answers:", answers);
        
        const correctCount = questions.reduce((count, q) => 
            answers[q.text] === q.correct ? count + 1 : count, 0
        );

        console.log("Score:", correctCount);
        
        setScore(correctCount);

        if (correctCount >= 7) {
            try {
                console.log("Fetching summary for:", course);
                
                const response = await axios.post("http://localhost:5001/generate-summary", { course });
                
                console.log("Summary received:", response.data);
                setSummary(response.data.summary);
            } catch (error) {
                console.error("Error generating summary:", error);
            }
        }
    };

    return (
        <div className="quiz-container">
            <h2>📚 {course} {level} Assessment</h2>

            {loading ? <p>Loading questions...</p> : (
                <div>
                    {questions.map((q, index) => (
                        <div key={index} className="question">
                            <p>{q.text}</p>
                            {q.options.map((option, idx) => (
                                <label key={idx} style={{ display: "block" }}>
                                    <input 
                                        type="radio" 
                                        name={`question-${index}`} 
                                        value={option} 
                                        checked={answers[q.text] === option}
                                        onChange={() => handleAnswerChange(q.text, option)}
                                    />
                                    {option}
                                </label>
                            ))}
                        </div>
                    ))}
                    <button className="submit-btn" onClick={handleSubmit}>Submit Answers</button>
                </div>
            )}

            {score !== null && (
                <div className="result">
                    <h3>Your Score: {score}/10</h3>
                    {score >= 7 ? (
                        <div>
                            <h4>📖 Summary of Next Level:</h4>
                            <p>{summary}</p>
                        </div>
                    ) : (
                        <p>⚠️ You need more practice before advancing.</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Assessment;
