import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Assessment.css";

// Question Bank
const questionsData = {
  Python: {
    Basic: [
      { question: "What is the output of `print(2 + 3)`?", options: ["5", "23", "Error"], answer: "5" },
      { question: "What is the keyword to define a function?", options: ["func", "def", "define"], answer: "def" },
      { question: "Which data type is immutable?", options: ["List", "Tuple", "Dictionary"], answer: "Tuple" },
    ],
    Intermediate: [
      { question: "What does `lambda x: x + 2` return?", options: ["Function", "2", "Error"], answer: "Function" },
      { question: "How to open a file in read mode?", options: ["open('file.txt')", "open('file.txt', 'r')", "read('file.txt')"], answer: "open('file.txt', 'r')" },
      { question: "Which library is used for data analysis?", options: ["NumPy", "Pandas", "Matplotlib"], answer: "Pandas" },
    ],
    Advanced: [
      { question: "What is the time complexity of sorting in Python?", options: ["O(n^2)", "O(log n)", "O(n log n)"], answer: "O(n log n)" },
    ]
  },
  Java: {
    Basic: [
      { question: "Which keyword is used to define a class?", options: ["class", "define", "struct"], answer: "class" },
      { question: "Which data type is used for whole numbers?", options: ["double", "float", "int"], answer: "int" },
      { question: "What is the default value of boolean in Java?", options: ["true", "false", "null"], answer: "false" },
    ],
    Intermediate: [
      { question: "Which collection framework class implements a dynamic array?", options: ["ArrayList", "LinkedList", "HashMap"], answer: "ArrayList" },
      { question: "How do you handle exceptions in Java?", options: ["if-else", "try-catch", "switch-case"], answer: "try-catch" },
      { question: "Which keyword is used to inherit a class?", options: ["extend", "inherits", "super"], answer: "extend" },
    ],
    Advanced: [
      { question: "Which Java feature allows multiple methods with the same name but different parameters?", options: ["Method Overriding", "Method Overloading", "Polymorphism"], answer: "Method Overloading" },
    ]
  }
};

// ‚úÖ Function to get random questions
const getRandomQuestions = (course) => {
  if (!questionsData[course]) return [];

  const basic = [...questionsData[course].Basic].sort(() => Math.random() - 0.5).slice(0, 3);
  const intermediate = [...questionsData[course].Intermediate].sort(() => Math.random() - 0.5).slice(0, 3);
  const advanced = [...questionsData[course].Advanced].sort(() => Math.random() - 0.5).slice(0, 1);

  const questions = [...basic, ...intermediate, ...advanced].sort(() => Math.random() - 0.5);
  console.log("Generated questions:", questions); // Debugging
  return questions;
};

const Assessment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || { course: "Python" }; // Default to Python if not set

  console.log("Course from location.state:", course); // Debugging

  const [questions, setQuestions] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [score, setScore] = useState(null);
  const [result, setResult] = useState("");

  useEffect(() => {
    console.log("Fetching questions for course:", course); // Debugging
    setQuestions(getRandomQuestions(course));
  }, [course]);

  // ‚úÖ Handle option selection
  const handleOptionSelect = (questionIndex, option) => {
    setSelectedOptions({ ...selectedOptions, [questionIndex]: option });
  };

  // ‚úÖ Handle form submission
  const handleSubmit = () => {
    let correctAnswers = 0;
    questions.forEach((q, index) => {
      if (selectedOptions[index] === q.answer) {
        correctAnswers++;
      }
    });

    console.log("Correct Answers:", correctAnswers); // Debugging

    setScore(correctAnswers);
    if (correctAnswers >= 5) {
      setResult("üéâ Passed! Redirecting to course...");
      setTimeout(() => {
        console.log("Course for redirection:", course); // Debugging
        navigate(`/course/${course}/1`); // Redirect to the first module
      }, 2000);
    } else {
      setResult("‚ùå Failed! Try again.");
    }
  };

  return (
    <div className="assessment-container">
      <h2>{course} Assessment</h2>
      {questions.length === 0 ? (
        <p>Loading questions...</p>
      ) : (
        questions.map((q, index) => (
          <div key={index} className="question-card">
            <p className="question-text">{q.question}</p>
            <div className="options-container">
              {q.options.map((option, i) => (
                <button
                  key={i}
                  className={`option-button ${selectedOptions[index] === option ? "selected" : ""}`}
                  onClick={() => handleOptionSelect(index, option)}
                >
                  {option}
                </button>
              ))}
            </div>
          </div>
        ))
      )}
      <button className="submit-button" onClick={handleSubmit}>
        Submit
      </button>
      {score !== null && (
        <div className="score-container">
          <p>Score: {score}/{questions.length}</p>
          <p className={`result-message ${score >= 5 ? "passed" : "failed"}`}>
            {result}
          </p>
        </div>
      )}
    </div>
  );
};

export default Assessment;
