import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

const quizData = {
  Python: {
    1: [
      { question: "Who created Python?", options: ["Dennis Ritchie", "Guido van Rossum", "James Gosling", "Bjarne Stroustrup"], answer: "Guido van Rossum" },
      { question: "Which Python version introduced pattern matching?", options: ["Python 2.7", "Python 3.6", "Python 3.9", "Python 3.10"], answer: "Python 3.10" },
      { question: "What is NOT a key feature of Python?", options: ["Readability", "Dynamically typed", "Complex syntax", "Extensive libraries"], answer: "Complex syntax" },
      { question: "What command is used to check the installed Python version?", options: ["python -v", "python --version", "check_python", "python_info"], answer: "python --version" },
      { question: "Which IDE is commonly used for professional Python development?", options: ["Eclipse", "PyCharm", "NetBeans", "IntelliJ IDEA"], answer: "PyCharm" },
      { question: "Which of the following is a valid variable name in Python?", options: ["123name", "my-variable", "name_123", "my var"], answer: "name_123" },
      { question: "What is the correct syntax for writing a single-line comment in Python?", options: ["// This is a comment", "# This is a comment", "/* This is a comment */", "-- This is a comment"], answer: "# This is a comment" },
      { question: "Which data type is used to store a sequence of characters in Python?", options: ["int", "str", "bool", "float"], answer: "str" },
      { question: "What will be the output of print(10 // 3)?", options: ["3.33", "3", "3.0", "4"], answer: "3" },
      { question: "What does the not operator do?", options: ["Converts a variable into a negative value", "Inverts a boolean expression", "Checks if a variable is null", "Performs bitwise negation"], answer: "Inverts a boolean expression" },
      { question: "What is the purpose of import math in Python?", options: ["Defines a new function", "Imports built-in mathematical functions", "Performs matrix operations", "Converts text to numbers"], answer: "Imports built-in mathematical functions" },
      { question: "What is the output of print(len('Hello'))?", options: ["4", "5", "Hello", "6"], answer: "5" },
      { question: "What is the purpose of an elif statement?", options: ["It is an alternative to an else statement", "It allows multiple conditions to be checked", "It is a shorthand for if-else", "It executes code when the if statement is false"], answer: "It allows multiple conditions to be checked" },
      { question: "Which loop is used when the number of iterations is not known beforehand?", options: ["for loop", "while loop", "do-while loop", "switch loop"], answer: "while loop" },
      { question: "What will happen if a break statement is encountered inside a loop?", options: ["It skips the current iteration and moves to the next one", "It exits the loop completely", "It continues execution without affecting the loop", "It throws an error"], answer: "It exits the loop completely" },
    ],
  },
};

const Quiz = () => {
  const { courseName, moduleId } = useParams();
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!quizData[courseName] || !quizData[courseName][moduleId]) {
      console.error("Quiz data not found!");
      setQuestions([]); // Ensure state is always defined
    } else {
      setQuestions(quizData[courseName][moduleId]);
    }
    setLoading(false);
  }, [courseName, moduleId]);

  if (loading) return <p>Loading...</p>;

  if (!questions.length) {
    return <p>No quiz available for this module.</p>;
  }

  return (
    <div>
      <h2>Quiz for Module {moduleId}</h2>
      {questions.map((q, index) => (
        <div key={index}>
          <p>{q.question}</p>
          <ul>
            {q.options.map((option, idx) => (
              <li key={idx}>{option}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default Quiz;
