import React from "react";
import { useLocation } from "react-router-dom";
import "./Assessment.css";

const Assessment = () => {
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const course = queryParams.get("course");
    const level = queryParams.get("level");

    return (
        <div className="assessment-container">
            <h1>{course} Assessment</h1>
            <h2>Level: {level}</h2>
            <p>Answer the following questions to assess your knowledge level.</p>
            
            {/* Example Questions (You can replace with real quiz logic) */}
            <div className="question">
                <p>1. What is a variable in {course}?</p>
                <input type="text" placeholder="Your Answer" />
            </div>
            <div className="question">
                <p>2. Write a simple program to print 'Hello World' in {course}.</p>
                <textarea placeholder="Your Code"></textarea>
            </div>
            
            <button className="submit-btn">Submit Assessment</button>
        </div>
    );
};

export default Assessment;
