import { useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import "./Quiz.css";

const Quiz = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { questions } = location.state || {};

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    setSelectedOption(null);
    setIsCorrect(null);
  }, [currentQuestionIndex]);

  if (!questions || questions.length === 0) {
    return <p className="no-quiz">No quiz available for this module.</p>;
  }

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setIsCorrect(option === questions[currentQuestionIndex].correct);
  };

  const handleNextQuestion = () => {
    if (selectedOption === questions[currentQuestionIndex].correct) {
      setScore(score + 1);
    }
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  const handleRestart = () => {
    setCurrentQuestionIndex(0);
    setSelectedOption(null);
    setScore(0);
    setShowResult(false);
  };

  if (showResult) {
    return (
      <div className="quiz-container result">
        <h2>Quiz Completed üéâ</h2>
        <p>Your Score: {score} / {questions.length}</p>
        <button onClick={handleRestart}>Restart Quiz üîÑ</button>
        <button onClick={() => navigate("/")}>Go Home üè†</button>
      </div>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-container">
      <h2>Question {currentQuestionIndex + 1} / {questions.length}</h2>
      <progress value={currentQuestionIndex + 1} max={questions.length}></progress>
      <p className="question-text">{currentQuestion.text}</p>
      <ul className="options-list">
        {currentQuestion.options.map((option, index) => (
          <li key={index} className={`option ${selectedOption === option ? (isCorrect ? "correct" : "wrong") : ""}`}>
            <label>
              <input
                type="radio"
                name="option"
                value={option}
                checked={selectedOption === option}
                onChange={() => handleOptionSelect(option)}
              />
              {option}
            </label>
          </li>
        ))}
      </ul>
      <button onClick={handleNextQuestion} disabled={selectedOption === null}>
        {currentQuestionIndex === questions.length - 1 ? "Finish ‚úÖ" : "Next ‚û°Ô∏è"}
      </button>
    </div>
  );
};

export default Quiz;
